  acceptchildmessage!: string;

  @ViewChild(PostComponent) childComponent:any;

  constructor() {}

  ngAfterViewInit() {
    // Accessing the property of the child component
    console.log(this.childComponent);
    this.acceptchildmessage = this.childComponent.childMessage;
  }
}
